/* --- Base Container --- */
.woocommerce-checkout form.checkout {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
    font-size: 17px;
    color: #1f2e1d; /* Deep green-black */
    background: linear-gradient(to bottom, #fdfdfd, #f7f7f7);
    padding: 80px 100px;
    border-radius: 20px;
    box-shadow: 0 20px 50px rgba(0, 0, 0, 0.05);
    max-width: 100%;
    line-height: 1.7;
}

/* --- Labels --- */
.woocommerce-checkout .form-row label {
    font-size: 16px;
    font-weight: 600;
    margin-bottom: 10px;
    display: block;
    color: #1f2e1d;
    letter-spacing: 0.3px;
}

/* --- Form Fields --- */
.woocommerce-checkout input.input-text,
.woocommerce-checkout textarea,
.woocommerce-checkout select {
    width: 100%;
    padding: 5px 5px 5px 5px;
    font-size: 17px;
    background: #fff;
    border: 1px solid #d7d7d7;
    border-radius: 14px;
    margin-bottom: 32px;
    transition: all 0.3s ease;
    box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.04);
}

.woocommerce-checkout input:focus,
.woocommerce-checkout textarea:focus,
.woocommerce-checkout select:focus {
    border-color: #3a5d37;
    outline: none;
    box-shadow: 0 0 0 4px rgba(58, 93, 55, 0.12);
}

/* --- Gold Accent for Required Fields --- */
.woocommerce-checkout .form-row label.required:after {
    content: "*";
    color: #c8a95c; /* Gold */
    margin-left: 5px;
    font-weight: bold;
}

/* --- Headings --- */
.woocommerce-checkout h2,
.woocommerce-checkout h3 {
    font-size: 26px;
    font-weight: 700;
    color: #1f2e1d;
    margin-bottom: 30px;
}

/* --- Order Review Table --- */
.woocommerce-checkout-review-order-table {
    background-color: #ffffff;
    border: 1px solid #e4e4e4;
    border-radius: 16px;
    padding: 32px;
    margin-top: 50px;
    font-size: 17px;
}

.woocommerce-checkout-review-order-table th,
.woocommerce-checkout-review-order-table td {
    padding: 20px;
    font-weight: 500;
    color: #1f2e1d;
}

/* --- Notices --- */
.woocommerce-error,
.woocommerce-message,
.woocommerce-info {
    font-size: 16px;
    background: #fdfaf6;
    color: #1f2e1d;
    border-left: 5px solid #c8a95c;
    padding: 20px 26px;
    border-radius: 14px;
    margin-bottom: 40px;
}


/* --- Responsive Layout --- */

@media (max-width: 768px) {
    .woocommerce-checkout form.checkout {
        padding: 24px 0px; /* Reduce padding for mobile */
        margin: 0; /* Remove outer margin */
        border-radius: 0; /* Optional: square corners for edge-to-edge feel */
        box-shadow: none; /* Optional: remove shadows on small screens */
    }

    .woocommerce-checkout .form-row,
    .woocommerce-checkout input.input-text,
    .woocommerce-checkout textarea,
    .woocommerce-checkout select {
        width: 100%;
    }

    .woocommerce-checkout button.button {
        width: 100%;
        padding: 20px;
        font-size: 17px;
    }
}


/*Remove Botton funcion*/

#order_review td.product-name {
    position: relative;
}

#order_review td.product-name > a.remove-checkout-item {
    position: absolute;
    right: 15px;
    top: 50%;
    transform: translateY(-50%);
    width: 32px;
    height: 32px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 18px;
    font-weight: bold;
    color: #fff;
    background-color: #c8a95c; /* Golden tone */
    border-radius: 50%;
    text-decoration: none;
    transition: all 0.3s ease;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.1);
}

#order_review td.product-name > a.remove-checkout-item:hover {
    background-color: #a17f42; /* Darker gold on hover */
    transform: translateY(-50%) scale(1.1);
    color: #fff;
}

/* Base Notification Style */
.notification {
  position: fixed;
  bottom: 20px;
  left: 20px;
  max-width: 380px;
  width: calc(100% - 40px);
  display: flex;
  align-items: center;
  background: #ffffff;
  border-radius: 14px;
  padding: 16px;
  box-shadow: 0 6px 20px rgba(0, 0, 0, 0.15);
  z-index: 1000;
  transition: all 0.4s cubic-bezier(0.2, 0, 0, 1);
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
  gap: 14px;
  border: 1px solid rgba(0, 0, 0, 0.05);
  backdrop-filter: blur(4px);
}

.notification img {
  width: 40px;
  height: 40px;
  border-radius: 10px;
  object-fit: cover;
  flex-shrink: 0;
}

.popup-text {
  font-size: 14px;
  color: #333;
  flex-grow: 1;
  line-height: 1.4;
}

.notification h2 {
  font-size: 15px;
  margin: 0 0 2px 0;
  font-weight: 600;
  color: #222;
}

.notification h3 {
  font-size: 13px;
  margin: 0;
  color: #666;
  font-weight: 500;
}

.notification p {
  font-size: 12px;
  margin: 4px 0 0 0;
  color: #888;
  line-height: 1.3;
}

/* Animation States */
.hidden {
  opacity: 0;
  pointer-events: none;
  transform: translateY(40px) scale(0.95);
}

.show {
  opacity: 1;
  pointer-events: auto;
  transform: translateY(0) scale(1);
}

/* Notification Types (optional) */
.notification.success {
  background: #f6ffed;
  border-left: 4px solid #52c41a;
}

.notification.error {
  background: #fff2f0;
  border-left: 4px solid #ff4d4f;
}

.notification.warning {
  background: #fffbe6;
  border-left: 4px solid #faad14;
}

/* Mobile devices (up to 480px width) */
@media (max-width: 480px) {
  .notification {
    bottom: 16px;
    left: 16px;
    right: auto;
    width: calc(100% - 32px);
    padding: 14px;
    gap: 12px;
  }

  .notification img {
    width: 36px;
    height: 36px;
  }
}

/* Tablets (up to 768px width) */
@media (min-width: 481px) and (max-width: 768px) {
  .notification {
    max-width: 400px;
  }
}

/* Larger screens (Desktops and Large Laptops) */
@media (min-width: 1025px) {
  .notification {
    bottom: 24px;
    left: 24px;
    max-width: 420px;
  }
}

